import React, { useEffect, useState } from "react";

function GaleriaProyectos() {
    const [proyectos, setProyectos] = useState([]);
    const [error, setError] = useState(false);

    useEffect(() => {
        fetch("http://localhost:5000/api/proyectos")
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Error al obtener proyectos");
                }
                return response.json();
            })
            .then((data) => {
                console.log("Proyectos recibidos:", data); // Log para verificar datos
                setProyectos(data);
            })
            .catch((error) => {
                console.error("Error al obtener proyectos:", error);
                setError(true);
            });
    }, []);

    if (error) {
        return <p>Error al cargar proyectos. Revisa la consola para más detalles.</p>;
    }

    if (proyectos.length === 0) {
        return <p>No hay proyectos disponibles.</p>;
    }

    return (
        <div>
            <h2>Galería de Proyectos</h2>
            {proyectos.map((proyecto) => (
                <div key={proyecto._id}>
                    <h3>{proyecto.titulo}</h3>
                    <p>{proyecto.descripcion}</p>
                    <small>Categoría: {proyecto.categoria}</small>
                </div>
            ))}
        </div>
    );
}

export default GaleriaProyectos;
