import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Para redirigir a la página de editar
import "bootstrap/dist/css/bootstrap.min.css"; // Importa Bootstrap

const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/premios');
        const data = await response.json();
        setPremios(data);
      } catch (err) {
        alert('Error al cargar los premios');
      }
    };

    fetchPremios();
  }, []);

  // Eliminar premio
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/premios/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setPremios(premios.filter((premio) => premio._id !== id));
        alert('Premio eliminado con éxito');
      } else {
        alert('Error al eliminar el premio');
      }
    } catch (err) {
      alert('Error al eliminar el premio');
    }
  };

  return (
    <div className="container mt-5">
      <h3 className="text-center mb-4">Lista de Premios</h3>
      {premios.length === 0 ? (
        <p className="text-center">No hay premios disponibles.</p>
      ) : (
        <table className="table table-striped table-bordered table-hover">
          <thead className="table-dark">
            <tr>
              <th>Tipo</th>
              <th>Descripción</th>
              <th>Marca</th>
              <th>Proveedor</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {premios.map((premio) => (
              <tr key={premio._id}>
                <td>{premio.tipo}</td>
                <td>{premio.descripcion}</td>
                <td>{premio.marca}</td>
                <td>{premio.proveedor.nombre} {premio.proveedor.apellido}</td>
                <td>
                  <Link
                    to={`/editar-premio/${premio._id}`}
                    className="btn btn-warning me-2"
                  >
                    Editar
                  </Link>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(premio._id)}
                  >
                    Eliminar
                  </button>


                  
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ListaPremios;
