import React, { useEffect, useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css"; // Importa Bootstrap

const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/premios');
        const data = await response.json();
        setPremios(data);
      } catch (err) {
        alert('Error al cargar los premios');
      }
    };

    fetchPremios();
  }, []);

  // Eliminar premio
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/premios/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setPremios(premios.filter((premio) => premio._id !== id));
        alert('Premio eliminado con éxito');
      } else {
        alert('Error al eliminar el premio');
      }
    } catch (err) {
      alert('Error al eliminar el premio');
    }
  };

  // Editar premio
  const handleEdit = (id) => {
    const premio = premios.find((premio) => premio._id === id);
    const nuevoTipo = prompt("Nuevo tipo de premio:", premio.tipo);
    const nuevaDescripcion = prompt("Nueva descripción:", premio.descripcion);
    const nuevaMarca = prompt("Nueva marca:", premio.marca);
    const nuevoProveedor = prompt("Nuevo proveedor (nombre apellido):", `${premio.proveedor.nombre} ${premio.proveedor.apellido}`);

    if (nuevoTipo && nuevaDescripcion && nuevaMarca && nuevoProveedor) {
      const [nombreProveedor, apellidoProveedor] = nuevoProveedor.split(" ");
      const updatedPremio = {
        tipo: nuevoTipo,
        descripcion: nuevaDescripcion,
        marca: nuevaMarca,
        proveedor: { nombre: nombreProveedor, apellido: apellidoProveedor },
      };

      fetch(`http://localhost:5000/api/premios/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedPremio),
      })
        .then((response) => response.json())
        .then((data) => {
          const updatedPremios = premios.map((premio) =>
            premio._id === id ? { ...premio, ...updatedPremio } : premio
          );
          setPremios(updatedPremios);
          alert('Premio actualizado con éxito');
        })
        .catch(() => alert('Error al actualizar el premio'));
    }
  };

  return (
    <div className="container mt-5">
      <h3 className="text-center mb-4">Lista de Premios</h3>
      {premios.length === 0 ? (
        <p className="text-center">No hay premios disponibles.</p>
      ) : (
        <table className="table table-striped table-bordered table-hover">
          <thead className="table-dark">
            <tr>
              <th>Tipo</th>
              <th>Descripción</th>
              <th>Marca</th>
              <th>Proveedor</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {premios.map((premio) => (
              <tr key={premio._id}>
                <td>{premio.tipo}</td>
                <td>{premio.descripcion}</td>
                <td>{premio.marca}</td>
                <td>{premio.proveedor.nombre} {premio.proveedor.apellido}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => handleEdit(premio._id)}>Editar</button>
                  <button className="btn btn-danger" onClick={() => handleDelete(premio._id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ListaPremios;
