// frontend/src/components/CrearPresupuesto.js
import React, { useState } from 'react';
import axios from 'axios';

function CrearPresupuesto() {
  const [formData, setFormData] = useState({
    nombre: '',
    monto: 0,
    origenFondos: '',
    destino: '',
    fechaFin: '',
  });

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/presupuestos', formData);
      alert('Presupuesto creado exitosamente');
      // Limpiar formulario o redirigir
      setFormData({
        nombre: '',
        monto: 0,
        origenFondos: '',
        destino: '',
        fechaFin: '',
      });
      // navigate('/presupuestos'); // si utilizas react-router
    } catch (error) {
      console.error(error);
      alert('Error al crear presupuesto');
    }
  };

  return (
    <div>
      <h2>Crear Presupuesto</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Monto:</label>
          <input
            type="number"
            name="monto"
            value={formData.monto}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Origen de Fondos:</label>
          <input
            type="text"
            name="origenFondos"
            value={formData.origenFondos}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Destino:</label>
          <input
            type="text"
            name="destino"
            value={formData.destino}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Fecha Fin (estimada):</label>
          <input
            type="date"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>
        <button type="submit">Crear</button>
      </form>
    </div>
  );
}

export default CrearPresupuesto;
