import React, { useEffect, useState } from "react";

function GaleriaProyectos() {
    const [proyectos, setProyectos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/proyectos") // Asegúrate de que tu backend esté corriendo
            .then((response) => response.json())
            .then((data) => setProyectos(data))
            .catch((error) => console.error("Error:", error));
    }, []);

    if (proyectos.length === 0) {
        return <p>No hay proyectos disponibles</p>;
    }

    return (
        <div>
            <h2>Galería de Proyectos</h2>
            {proyectos.map((proyecto) => (
                <div key={proyecto._id}>
                    <h3>{proyecto.titulo}</h3>
                    <p>{proyecto.descripcion}</p>
                    <small>Categoría: {proyecto.categoria}</small>
                </div>
            ))}
        </div>
    );
}

export default GaleriaProyectos;
