// frontend/src/components/CrearCarretera.js
import React, { useState } from 'react';
import axios from 'axios';

function CrearCarretera() {
  const [formData, setFormData] = useState({
    nombre: '',
    ubicacion: '',
    longitud: 0,
    presupuesto: 0,
    constructora: '',
    fechaFin: '',
  });

  const onChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/carreteras', formData);
      alert('Carretera creada exitosamente');
      // Podrías redirigir a /carreteras
      setFormData({
        nombre: '',
        ubicacion: '',
        longitud: 0,
        presupuesto: 0,
        constructora: '',
        fechaFin: '',
      });
    } catch (error) {
      console.error(error);
      alert('Error al crear carretera');
    }
  };

  return (
    <div>
      <h2>Crear Carretera</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Ubicación:</label>
          <input
            type="text"
            name="ubicacion"
            value={formData.ubicacion}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Longitud (km):</label>
          <input
            type="number"
            name="longitud"
            value={formData.longitud}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Presupuesto ($):</label>
          <input
            type="number"
            name="presupuesto"
            value={formData.presupuesto}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Constructora:</label>
          <input
            type="text"
            name="constructora"
            value={formData.constructora}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Fecha Fin (estimada):</label>
          <input
            type="date"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>
        <button type="submit">Crear</button>
      </form>
    </div>
  );
}

export default CrearCarretera;
