import React, { useEffect, useState } from "react";

function CrearGaleria() {
    const [expositores, setExpositores] = useState([]);
    const [expositorId, setExpositorId] = useState("");
    const [titulo, setTitulo] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [imagen, setImagen] = useState(null);

    // Cargar la lista de expositores
    useEffect(() => {
        fetch("http://localhost:5000/api/expositores")
            .then((res) => res.json())
            .then((data) => setExpositores(data))
            .catch((err) => console.error("Error al obtener expositores:", err));
    }, []);

    const manejarEnvio = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("expositorId", expositorId);
        formData.append("titulo", titulo);
        formData.append("descripcion", descripcion);
        formData.append("imagen", imagen);

        fetch("http://localhost:5000/api/contenidos/crear", {
            method: "POST",
            body: formData,
        })
            .then((res) => res.json())
            .then(() => {
                alert("Galería creada con éxito");
                setExpositorId("");
                setTitulo("");
                setDescripcion("");
                setImagen(null);
            })
            .catch((err) => console.error("Error al crear galería:", err));
    };

    return (
        <div>
            <h2>Crear Galería</h2>
            <form onSubmit={manejarEnvio}>
                <label>Seleccionar Expositor:</label>
                <select
                    value={expositorId}
                    onChange={(e) => setExpositorId(e.target.value)}
                    required
                >
                    <option value="">Seleccionar</option>
                    {expositores.map((expositor) => (
                        <option key={expositor._id} value={expositor._id}>
                            {expositor.nombre} {expositor.apellido}
                        </option>
                    ))}
                </select>

                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                />
                <input
                    type="file"
                    onChange={(e) => setImagen(e.target.files[0])}
                    required
                />
                <button type="submit">Crear Galería</button>
            </form>
        </div>
    );
}

export default CrearGaleria;
