import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

function EditarExpositor() {
    const { id } = useParams();
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [fecha, setFecha] = useState("");
    const [concepto, setConcepto] = useState("");
    const navigate = useNavigate();

    // Cargar los datos del expositor
    useEffect(() => {
        fetch(`http://localhost:5000/api/expositores/${id}`)
            .then((res) => res.json())
            .then((data) => {
                setNombre(data.nombre);
                setApellido(data.apellido);
                setFecha(data.fecha.split("T")[0]);
                setConcepto(data.concepto);
            })
            .catch((err) => console.error("Error al cargar expositor:", err));
    }, [id]);

    const manejarEnvio = (e) => {
        e.preventDefault();
        const expositorActualizado = { nombre, apellido, fecha, concepto };

        fetch(`http://localhost:5000/api/expositores/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(expositorActualizado),
        })
            .then((res) => res.json())
            .then(() => {
                alert("Expositor actualizado con éxito");
                navigate("/expositores");
            })
            .catch((err) => console.error("Error al actualizar expositor:", err));
    };

    return (
        <div>
            <h2>Editar Expositor</h2>
            <form onSubmit={manejarEnvio}>
                <input
                    type="text"
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Apellido"
                    value={apellido}
                    onChange={(e) => setApellido(e.target.value)}
                    required
                />
                <input
                    type="date"
                    value={fecha}
                    onChange={(e) => setFecha(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Concepto"
                    value={concepto}
                    onChange={(e) => setConcepto(e.target.value)}
                    required
                />
                <button type="submit">Actualizar</button>
            </form>
        </div>
    );
}

export default EditarExpositor;
