import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import $ from "jquery";
import "datatables.net-dt";

function ListaGaleria() {
    const [contenidos, setContenidos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/contenidos")
            .then((res) => res.json())
            .then((data) => {
                setContenidos(data);
                setTimeout(() => {
                    $("#tablaGaleria").DataTable(); // Iniciar DataTables
                }, 500);
            })
            .catch((err) => console.error("Error al obtener contenidos:", err));
    }, []);

    // Función para eliminar contenido
    const eliminarContenido = (id) => {
        if (window.confirm("¿Estás seguro de que deseas eliminar esta galería?")) {
            fetch(`http://localhost:5000/api/contenidos/${id}`, { method: "DELETE" })
                .then((res) => res.json())
                .then(() => {
                    alert("Galería eliminada con éxito");
                    setContenidos(contenidos.filter(contenido => contenido._id !== id)); // Actualiza la tabla
                })
                .catch((err) => console.error("Error al eliminar contenido:", err));
        }
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center">Lista de Galerías</h2>
            <div className="table-responsive">
                <table id="tablaGaleria" className="table table-striped table-bordered">
                    <thead className="table-dark">
                        <tr>
                            <th>Título</th>
                            <th>Descripción</th>
                            <th>Expositor</th>
                            <th>Imagen</th>
                            <th>Fecha</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {contenidos.length === 0 ? (
                            <tr>
                                <td colSpan="6" className="text-center">No hay contenidos disponibles.</td>
                            </tr>
                        ) : (
                            contenidos.map((contenido) => (
                                <tr key={contenido._id}>
                                    <td>{contenido.titulo}</td>
                                    <td>{contenido.descripcion}</td>
                                    <td>{contenido.expositor ? `${contenido.expositor.nombre} ${contenido.expositor.apellido}` : "Sin asignar"}</td>
                                    <td>
                                        <img
                                            src={`http://localhost:5000${contenido.imagen}`}
                                            alt={contenido.titulo}
                                            className="img-fluid"
                                            style={{ maxWidth: "150px" }}
                                        />
                                    </td>
                                    <td>{new Date(contenido.fecha).toLocaleDateString()}</td>
                                    <td>
                                        <button 
                                            className="btn btn-danger btn-sm"
                                            onClick={() => eliminarContenido(contenido._id)}
                                        >
                                            Eliminar
                                        </button>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ListaGaleria;
