import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css"; // Estilos de Bootstrap
import "datatables.net-dt/css/jquery.dataTables.min.css"; // DataTables Estilos
import "datatables.net-buttons-dt/css/buttons.dataTables.min.css"; // Botones de Exportación

import $ from "jquery";
import "datatables.net";
import "datatables.net-buttons";
import "datatables.net-buttons/js/buttons.html5";
import "datatables.net-buttons/js/buttons.print";
import "jszip";
import "pdfmake";

// **Evitar el error de fuentes en pdfMake**
import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

function ListaGaleria() {
    const [contenidos, setContenidos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/contenidos")
            .then((res) => res.json())
            .then((data) => {
                setContenidos(data);
                setTimeout(() => {
                    if (!$.fn.DataTable.isDataTable("#tablaGaleria")) {
                        $("#tablaGaleria").DataTable({
                            dom: "Bfrtip",
                            buttons: ["copy", "csv", "excel", "pdf", "print"],
                            destroy: true, // Permite reinicializar
                            language: {
                                url: "//cdn.datatables.net/plug-ins/1.10.25/i18n/Spanish.json"
                            }
                        });
                    }
                }, 100);
            })
            .catch((err) => console.error("Error al obtener contenidos:", err));
    }, []);

    // Función para eliminar contenido
    const eliminarContenido = (id) => {
        if (window.confirm("¿Estás seguro de que deseas eliminar esta galería?")) {
            fetch(`http://localhost:5000/api/contenidos/${id}`, { method: "DELETE" })
                .then((res) => res.json())
                .then(() => {
                    alert("Galería eliminada con éxito");
                    setContenidos(contenidos.filter(contenido => contenido._id !== id));
                })
                .catch((err) => console.error("Error al eliminar contenido:", err));
        }
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center">Lista de Galerías</h2>
            <table id="tablaGaleria" className="table table-striped">
                <thead>
                    <tr>
                        <th>Título</th>
                        <th>Descripción</th>
                        <th>Expositor</th>
                        <th>Imagen</th>
                        <th>Fecha</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {contenidos.map((contenido) => (
                        <tr key={contenido._id}>
                            <td>{contenido.titulo}</td>
                            <td>{contenido.descripcion}</td>
                            <td>
                                {contenido.expositor
                                    ? `${contenido.expositor.nombre} ${contenido.expositor.apellido}`
                                    : "Sin asignar"}
                            </td>
                            <td>
                                <img
                                    src={`http://localhost:5000${contenido.imagen}`}
                                    alt={contenido.titulo}
                                    style={{ width: "100px" }}
                                />
                            </td>
                            <td>{new Date(contenido.fecha).toLocaleDateString()}</td>
                            <td>
                                <button
                                    className="btn btn-danger"
                                    onClick={() => eliminarContenido(contenido._id)}
                                >
                                    Eliminar
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ListaGaleria;
