import React, { useState } from "react";

function SubirContenido() {
    const [archivo, setArchivo] = useState(null);
    const [descripcion, setDescripcion] = useState("");

    const manejarEnvio = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("imagen", archivo);
        formData.append("descripcion", descripcion);

        try {
            const respuesta = await fetch(`http://localhost:5000/api/proyectos/:id/imagen`, {
                method: "POST",
                body: formData,
            });
            const resultado = await respuesta.json();
            console.log("Contenido subido:", resultado);
        } catch (error) {
            console.error("Error al subir contenido:", error);
        }
    };

    return (
        <form onSubmit={manejarEnvio}>
            <input type="file" onChange={(e) => setArchivo(e.target.files[0])} required />
            <textarea placeholder="DescripciÃ³n" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
            <button type="submit">Subir Contenido</button>
        </form>
    );
}

export default SubirContenido;
