// frontend/src/components/ListaProyectos.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function ListaProyectos() {
  const [proyectos, setProyectos] = useState([]);

  const getProyectos = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/proyectos');
      setProyectos(res.data);
    } catch (error) {
      console.error(error);
      alert('Error al cargar proyectos');
    }
  };

  useEffect(() => {
    getProyectos();
  }, []);

  const eliminarProyecto = async (id) => {
    if (!window.confirm('¿Desea eliminar este proyecto?')) return;
    try {
      await axios.delete(`http://localhost:4000/api/proyectos/${id}`);
      alert('Proyecto eliminado');
      getProyectos();
    } catch (error) {
      console.error(error);
      alert('Error al eliminar');
    }
  };

  return (
    <div>
      <h2>Lista de Proyectos</h2>
      <Link to="/crear-proyecto">
        <button>Crear Proyecto</button>
      </Link>
      {proyectos.length === 0 ? (
        <p>No hay proyectos registrados</p>
      ) : (
        <table border="1" cellPadding="5" style={{ marginTop: '10px' }}>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Contratista</th>
              <th>Presupuesto</th>
              <th>Carretera</th>
              <th>Fecha Inicio</th>
              <th>Fecha Fin</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {proyectos.map((p) => (
              <tr key={p._id}>
                <td>{p.nombre}</td>
                {/* p.contratista?.nombre => si "contratista" no es null, muestra su nombre */}
                <td>{p.contratista?.nombre || ''}</td>
                
                {/* Ej. "Presupuesto 2025 - $100000" */}
                <td>
                  {p.presupuesto
                    ? `${p.presupuesto.monto} - $${p.presupuesto.monto}`
                    : ''}
                </td>

                <td>{p.carretera?.nombre || ''}</td>
                <td>
                  {p.fechaInicio
                    ? new Date(p.fechaInicio).toLocaleDateString()
                    : ''}
                </td>
                <td>
                  {p.fechaFin
                    ? new Date(p.fechaFin).toLocaleDateString()
                    : ''}
                </td>

                <td>
                  <Link to={`/editar-proyecto/${p._id}`}>
                    <button>Editar</button>
                  </Link>
                  <button onClick={() => eliminarProyecto(p._id)}>
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ListaProyectos;
