import React, { useState } from "react";

function SubirContenido({ expositorId }) {
    const [titulo, setTitulo] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [imagen, setImagen] = useState(null);

    const manejarEnvio = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append("titulo", titulo);
        formData.append("descripcion", descripcion);
        formData.append("imagen", imagen);

        fetch(`http://localhost:5000/api/contenidos/${expositorId}`, {
            method: "POST",
            body: formData,
        })
            .then((res) => res.json())
            .then(() => {
                alert("Contenido subido con éxito");
                setTitulo("");
                setDescripcion("");
                setImagen(null);
            })
            .catch((err) => console.error("Error al subir contenido:", err));
    };

    return (
        <div>
            <h2>Subir Contenido</h2>
            <form onSubmit={manejarEnvio}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                />
                <input
                    type="file"
                    onChange={(e) => setImagen(e.target.files[0])}
                    required
                />
                <button type="submit">Subir</button>
            </form>
        </div>
    );
}

export default SubirContenido;
