import React, { useState } from 'react';
import axios from 'axios';

const FormularioPremio = () => {
  const [tipo, setTipo] = useState('efectivo');
  const [descripcion, setDescripcion] = useState('');
  const [marca, setMarca] = useState('');
  const [nombreProveedor, setNombreProveedor] = useState('');
  const [apellidoProveedor, setApellidoProveedor] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const nuevoPremio = {
      tipo,
      descripcion,
      marca,
      proveedor: {
        nombre: nombreProveedor,
        apellido: apellidoProveedor
      }
    };

    try {
      await axios.post('http://localhost:5000/premios', nuevoPremio);
      alert('Premio agregado con éxito');
    } catch (err) {
      alert('Error al agregar el premio');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Tipo:
        <select value={tipo} onChange={(e) => setTipo(e.target.value)}>
          <option value="efectivo">Efectivo</option>
          <option value="obsequio">Obsequio</option>
        </select>
      </label>
      <br />
      <label>
        Descripción:
        <input type="text" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />
      </label>
      <br />
      <label>
        Marca:
        <input type="text" value={marca} onChange={(e) => setMarca(e.target.value)} required />
      </label>
      <br />
      <label>
        Nombre del Proveedor:
        <input type="text" value={nombreProveedor} onChange={(e) => setNombreProveedor(e.target.value)} required />
      </label>
      <br />
      <label>
        Apellido del Proveedor:
        <input type="text" value={apellidoProveedor} onChange={(e) => setApellidoProveedor(e.target.value)} required />
      </label>
      <br />
      <button type="submit">Agregar Premio</button>
    </form>
  );
};

export default FormularioPremio;
