import React, { useEffect, useState } from "react";

function VerProyecto({ proyectoId }) {
    const [proyecto, setProyecto] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:5000/api/proyectos/${proyectoId}`)
            .then((res) => res.json())
            .then((data) => setProyecto(data))
            .catch((err) => console.error("Error al obtener proyecto:", err));
    }, [proyectoId]);

    if (!proyecto) return <p>Cargando...</p>;

    return (
        <div>
            <h1>{proyecto.titulo}</h1>
            <p>{proyecto.descripcion}</p>
            <h3>Im√°genes</h3>
            {proyecto.imagenes.map((imagen) => (
                <img key={imagen._id} src={imagen.url} alt={imagen.descripcion} />
            ))}
            <h3>Videos</h3>
            {proyecto.videos.map((video) => (
                <video key={video._id} controls>
                    <source src={video.url} type="video/mp4" />
                </video>
            ))}
        </div>
    );
}

export default VerProyecto;
