// frontend/src/components/EditarCarretera.js
import React, { useState, useEffect } from 'react'; 
// ↑ Importamos React y los hooks necesarios

import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditarCarretera() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nombre: '',
    ubicacion: '',
    longitud: 0,
    fechaFin: '',
  });

  useEffect(() => {
    const getCarreteraById = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/carreteras/${id}`);
        if (res.data) {
          setFormData({
            nombre: res.data.nombre,
            ubicacion: res.data.ubicacion,
            longitud: res.data.longitud,
            fechaFin: res.data.fechaFin ? res.data.fechaFin.substring(0, 10) : '',
          });
        }
      } catch (error) {
        console.error(error);
      }
    };
    getCarreteraById();
  }, [id]);

  const onChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/carreteras/${id}`, formData);
      alert('Carretera actualizada');
      navigate('/carreteras');
    } catch (error) {
      console.error(error);
      alert('Error al actualizar');
    }
  };

  return (
    <div>
      <h2>Editar Carretera</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Ubicación:</label>
          <input
            type="text"
            name="ubicacion"
            value={formData.ubicacion}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Longitud (km):</label>
          <input
            type="number"
            name="longitud"
            value={formData.longitud}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Fecha de Entrega (estimada):</label>
          <input
            type="date"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>
        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
}

export default EditarCarretera;
