// frontend/src/components/ListaPresupuesto.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function ListaPresupuesto() {
  const [presupuestos, setPresupuestos] = useState([]);

  const getPresupuestos = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/presupuestos');
      setPresupuestos(res.data);
    } catch (error) {
      console.error(error);
      alert('Error al obtener presupuestos');
    }
  };

  useEffect(() => {
    getPresupuestos();
  }, []);

  const eliminarPresupuesto = async (id) => {
    if (!window.confirm('¿Deseas eliminar este presupuesto?')) return;
    try {
      await axios.delete(`http://localhost:4000/api/presupuestos/${id}`);
      alert('Presupuesto eliminado');
      getPresupuestos();
    } catch (error) {
      console.error(error);
      alert('Error al eliminar presupuesto');
    }
  };

  return (
    <div>
      <h2>Lista de Presupuestos</h2>
      <Link to="/crear-presupuesto">
        <button>Crear Presupuesto</button>
      </Link>
      {presupuestos.length === 0 ? (
        <p>No hay presupuestos registrados</p>
      ) : (
        <table border="1" cellPadding="5" style={{ marginTop: '10px' }}>
          <thead>
            <tr>
              
              <th>Monto</th>
              <th>Origen de Fondos</th>
              
              <th>Fecha de Entrega</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {presupuestos.map((item) => (
              <tr key={item._id}>
                <td>{item.nombre}</td>
                <td>{item.monto}</td>
                <td>{item.origenFondos}</td>
                <td>{item.destino}</td>
                <td>
                  {item.fechaFin
                    ? new Date(item.fechaFin).toLocaleDateString()
                    : 'Sin definir'}
                </td>
                <td>
                  <Link to={`/editar-presupuesto/${item._id}`}>
                    <button>Editar</button>
                  </Link>
                  <button onClick={() => eliminarPresupuesto(item._id)}>
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ListaPresupuesto;
