import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import $ from "jquery";  // Asegúrate de importar jQuery
import "datatables.net-dt";
import "datatables.net-buttons-dt";
import "jszip";
import "pdfmake";
import "datatables.net-buttons/js/buttons.html5";
import "datatables.net-buttons/js/buttons.print";

function ListaExpositores() {
    const [expositores, setExpositores] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetch("http://localhost:5000/api/expositores")
            .then((res) => res.json())
            .then((data) => {
                setExpositores(data);
                setTimeout(() => {
                    if (!$.fn.DataTable.isDataTable("#tablaExpositores")) {
                        $("#tablaExpositores").DataTable({
                            destroy: true,
                            responsive: true,
                            dom: 'Bfrtip',
                            buttons: [
                                'copy', 'csv', 'excel', 'pdf', 'print'
                            ],
                            language: {
                                url: "https://cdn.datatables.net/plug-ins/1.11.5/i18n/Spanish.json"
                            }
                        });
                    }
                }, 500);
            })
            .catch((err) => console.error("Error al obtener expositores:", err));
    }, []);

    const eliminarExpositor = (id) => {
        if (window.confirm("¿Estás seguro de eliminar este expositor?")) {
            fetch(`http://localhost:5000/api/expositores/${id}`, {
                method: "DELETE",
            })
                .then((res) => res.json())
                .then(() => {
                    setExpositores(expositores.filter((expositor) => expositor._id !== id));
                    alert("Expositor eliminado correctamente");
                })
                .catch((err) => console.error("Error al eliminar expositor:", err));
        }
    };

    const editarExpositor = (id) => {
        navigate(`/editar/${id}`);
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center">Lista de Expositores</h2>
            <button className="btn btn-primary mb-3" onClick={() => navigate("/crear")}>Crear Expositor</button>
            <div className="table-responsive">
                <table id="tablaExpositores" className="table table-striped table-bordered">
                    <thead className="table-dark">
                        <tr>
                            <th>Nombre</th>
                            <th>Apellido</th>
                            <th>Concepto</th>
                            <th>Fecha</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {expositores.length === 0 ? (
                            <tr>
                                <td colSpan="5" className="text-center">No hay expositores disponibles.</td>
                            </tr>
                        ) : (
                            expositores.map((expositor) => (
                                <tr key={expositor._id}>
                                    <td>{expositor.nombre}</td>
                                    <td>{expositor.apellido}</td>
                                    <td>{expositor.concepto}</td>
                                    <td>{new Date(expositor.fecha).toLocaleDateString()}</td>
                                    <td>
                                        <button 
                                            className="btn btn-warning btn-sm" 
                                            onClick={() => editarExpositor(expositor._id)}>
                                            Editar
                                        </button>
                                        <button 
                                            className="btn btn-danger btn-sm ms-2" 
                                            onClick={() => eliminarExpositor(expositor._id)}>
                                            Eliminar
                                        </button>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ListaExpositores;
