import React, { useEffect, useState } from 'react';
import './ListaPremios.css'; // Importamos el archivo CSS

const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/premios');
        const data = await response.json();
        setPremios(data);
      } catch (err) {
        alert('Error al cargar los premios');
      }
    };

    fetchPremios();
  }, []);

  return (
    <div className="premios-container">
      <h3 className="titulo">Lista de Premios</h3>
      {premios.length === 0 ? (
        <p className="no-premios">No hay premios disponibles.</p>
      ) : (
        <ul className="premios-list">
          {premios.map((premio) => (
            <li className="premio-item" key={premio._id}>
              <div className="premio-info">
                <p><strong>Tipo:</strong> {premio.tipo}</p>
                <p><strong>Descripci√≥n:</strong> {premio.descripcion}</p>
                <p><strong>Marca:</strong> {premio.marca}</p>
                <p><strong>Proveedor:</strong> {premio.proveedor.nombre} {premio.proveedor.apellido}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ListaPremios;
