import React, { useEffect, useState } from "react";

function ListaGaleria() {
    const [contenidos, setContenidos] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/api/contenidos")
            .then((res) => res.json())
            .then((data) => setContenidos(data))
            .catch((err) => console.error("Error al obtener contenidos:", err));
    }, []);

    // Función para eliminar contenido
    const eliminarContenido = (id) => {
        if (window.confirm("¿Estás seguro de que deseas eliminar esta galería?")) {
            fetch(`http://localhost:5000/api/contenidos/${id}`, { method: "DELETE" })
                .then((res) => res.json())
                .then(() => {
                    alert("Galería eliminada con éxito");
                    setContenidos(contenidos.filter(contenido => contenido._id !== id)); // Actualiza la lista
                })
                .catch((err) => console.error("Error al eliminar contenido:", err));
        }
    };

    return (
        <div>
            <h2>Lista de Galerías</h2>
            {contenidos.length === 0 ? (
                <p>No hay contenidos disponibles.</p>
            ) : (
                <ul>
                    {contenidos.map((contenido) => (
                        <li key={contenido._id}>
                            <h3>{contenido.titulo}</h3>
                            <p>{contenido.descripcion}</p>
                            <p>
                                <strong>Expositor:</strong>{" "}
                                {contenido.expositor
                                    ? `${contenido.expositor.nombre} ${contenido.expositor.apellido}`
                                    : "Sin asignar"}
                            </p>
                            <img
                                src={`http://localhost:5000${contenido.imagen}`}
                                alt={contenido.titulo}
                                style={{ width: "200px" }}
                            />
                            <p>
                                <strong>Fecha:</strong>{" "}
                                {new Date(contenido.fecha).toLocaleDateString()}
                            </p>
                            {/* Botón para eliminar contenido */}
                            <button
                                onClick={() => eliminarContenido(contenido._id)}
                                style={{
                                    backgroundColor: "red",
                                    color: "white",
                                    border: "none",
                                    padding: "10px",
                                    cursor: "pointer",
                                    marginTop: "10px"
                                }}
                            >
                                Eliminar
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default ListaGaleria;
