import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function ListaExpositores() {
    const [expositores, setExpositores] = useState([]);
    const navigate = useNavigate();

    // Obtener todos los expositores
    useEffect(() => {
        fetch("http://localhost:5000/api/expositores")
            .then((res) => res.json())
            .then((data) => setExpositores(data))
            .catch((err) => console.error("Error al obtener expositores:", err));
    }, []);

    // Función para eliminar un expositor
    const eliminarExpositor = (id) => {
        if (window.confirm("¿Estás seguro de eliminar este expositor?")) {
            fetch(`http://localhost:5000/api/expositores/${id}`, {
                method: "DELETE",
            })
                .then((res) => res.json())
                .then(() => {
                    setExpositores(expositores.filter((expositor) => expositor._id !== id));
                    alert("Expositor eliminado correctamente");
                })
                .catch((err) => console.error("Error al eliminar expositor:", err));
        }
    };

    // Función para redirigir a la página de edición
    const editarExpositor = (id) => {
        navigate(`/editar/${id}`);
    };

    return (
        <div>
            <h2>Lista de Expositores</h2>
            <button onClick={() => navigate("/crear")}>Crear Expositor</button>
            {expositores.length === 0 ? (
                <p>No hay expositores disponibles.</p>
            ) : (
                <ul>
                    {expositores.map((expositor) => (
                        <li key={expositor._id}>
                            {expositor.nombre} {expositor.apellido} - {expositor.concepto} (
                            {new Date(expositor.fecha).toLocaleDateString()})
                            <button onClick={() => editarExpositor(expositor._id)}>Editar</button>
                            <button onClick={() => eliminarExpositor(expositor._id)}>Eliminar</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default ListaExpositores;
