// frontend/src/components/EditarCarretera.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditarCarretera() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nombre: '',
    ubicacion: '',
    longitud: 0,
    presupuesto: 0,
    constructora: '',
    fechaFin: '',
  });

  useEffect(() => {
    const getCarreteraById = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/carreteras/${id}`);
        // Prellenar el formulario
        setFormData({
          nombre: res.data.nombre,
          ubicacion: res.data.ubicacion,
          longitud: res.data.longitud,
          presupuesto: res.data.presupuesto,
          constructora: res.data.constructora || '',
          fechaFin: res.data.fechaFin ? res.data.fechaFin.substring(0, 10) : '',
        });
      } catch (error) {
        console.error(error);
      }
    };
    getCarreteraById();
  }, [id]);

  const onChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/carreteras/${id}`, formData);
      alert('Carretera actualizada');
      navigate('/carreteras');
    } catch (error) {
      console.error(error);
      alert('Error al actualizar');
    }
  };

  return (
    <div>
      <h2>Editar Carretera</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Ubicaci√≥n:</label>
          <input
            type="text"
            name="ubicacion"
            value={formData.ubicacion}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Longitud (km):</label>
          <input
            type="number"
            name="longitud"
            value={formData.longitud}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Presupuesto ($):</label>
          <input
            type="number"
            name="presupuesto"
            value={formData.presupuesto}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Constructora:</label>
          <input
            type="text"
            name="constructora"
            value={formData.constructora}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Fecha Fin (estimada):</label>
          <input
            type="date"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>
        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
}

export default EditarCarretera;
