import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      const response = await axios.get('http://localhost:5000/premios');
      setPremios(response.data);
    };
    fetchPremios();
  }, []);

  return (
    <div>
      <h3>Premios</h3>
      <ul>
        {premios.map((premio) => (
          <li key={premio._id}>
            {premio.tipo} - {premio.descripcion} - {premio.marca} - {premio.proveedor.nombre} {premio.proveedor.apellido}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListaPremios;
