import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/premios');
        setPremios(response.data);
      } catch (err) {
        alert('Error al cargar los premios');
      }
    };

    fetchPremios();
  }, []);

  return (
    <div>
      <h3>Lista de Premios</h3>
      {premios.length === 0 ? (
        <p>No hay premios disponibles.</p>
      ) : (
        <ul>
          {premios.map((premio) => (
            <li key={premio._id}>
              <strong>Tipo:</strong> {premio.tipo} <br />
              <strong>Descripci√≥n:</strong> {premio.descripcion} <br />
              <strong>Marca:</strong> {premio.marca} <br />
              <strong>Proveedor:</strong> {premio.proveedor.nombre} {premio.proveedor.apellido}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ListaPremios;
