import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function VerProyecto() {
    const { id } = useParams(); // Obtén el ID de la URL
    const [proyecto, setProyecto] = useState(null);
    const [error, setError] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:5000/api/proyectos/${id}`)
            .then((res) => {
                if (!res.ok) {
                    throw new Error("Error al obtener proyecto");
                }
                return res.json();
            })
            .then((data) => setProyecto(data))
            .catch((err) => {
                console.error("Error al obtener proyecto:", err.message);
                setError(true);
            });
    }, [id]);

    if (error) {
        return <p>Error al cargar el proyecto. Revisa la consola para más detalles.</p>;
    }

    if (!proyecto) {
        return <p>Cargando proyecto...</p>;
    }

    return (
        <div>
            <h1>{proyecto.titulo}</h1>
            <p>{proyecto.descripcion}</p>
            <h3>Imágenes</h3>
            {proyecto.imagenes.map((imagen) => (
                <img key={imagen._id} src={imagen.url} alt={imagen.descripcion} />
            ))}
            <h3>Videos</h3>
            {proyecto.videos.map((video) => (
                <video key={video._id} controls>
                    <source src={video.url} type="video/mp4" />
                </video>
            ))}
        </div>
    );
}

export default VerProyecto;
