import React from "react";
import SubirContenido from "./components/SubirContenido";
import VerProyecto from "./components/VerProyecto";



function App() {
    const proyectoId = "ID_DEL_PROYECTO"; // Cambiar por un ID válido de tu base de datos

    return (
        <div>
            <h1>Galería de Proyectos</h1>
            <SubirContenido proyectoId={proyectoId} />
            <VerProyecto proyectoId={proyectoId} />
        </div>
    );
}

export default App;


router.get("/:id", async (req, res) => {
    try {
        const proyecto = await Proyecto.findById(req.params.id)
            .populate("imagenes")
            .populate("videos");

        if (!proyecto) {
            return res.status(404).json({ error: "Proyecto no encontrado" });
        }

        res.json(proyecto);
    } catch (err) {
        console.error("Error al obtener proyecto:", err.message);
        res.status(500).json({ error: "Error al obtener proyecto" });
    }
});
