import React, { useEffect, useState } from 'react';
import './ListaPremios.css';  
import "bootstrap/dist/css/bootstrap.min.css";


const ListaPremios = () => {
  const [premios, setPremios] = useState([]);

  useEffect(() => {
    const fetchPremios = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/premios');
        const data = await response.json();
        setPremios(data);
      } catch (err) {
        alert('Error al cargar los premios');
      }
    };

    fetchPremios();
  }, []);

  return (
    <div className="premios-container">
      <h3 className="text-center">Lista de Premios</h3>
      {premios.length === 0 ? (
        <p className="no-premios">No hay premios disponibles.</p>
      ) : (
        <table className="premios-table">
          <thead className="table-dark">
            <tr>
              <th>Tipo</th>
              <th>Descripci√≥n</th>
              <th>Marca</th>
              <th>Proveedor</th>
            </tr>
          </thead>
          <tbody>
            {premios.map((premio) => (
              <tr key={premio._id}>
                <td>{premio.tipo}</td>
                <td>{premio.descripcion}</td>
                <td>{premio.marca}</td>
                <td>{premio.proveedor.nombre} {premio.proveedor.apellido}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ListaPremios;
