// frontend/src/components/CrearConstructora.js
import React, { useState } from 'react';
import axios from 'axios';

function CrearConstructora() {
  const [formData, setFormData] = useState({
    nombre: '',
    direccion: '',
    telefono: '',
    representante: '',
  });

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/constructoras', formData);
      alert('Constructora creada exitosamente');
      // Reseteamos formulario
      setFormData({
        nombre: '',
        direccion: '',
        telefono: '',
        representante: '',
      });
      // Podrías redirigir a la lista
      // Ej: navigate('/constructoras');
    } catch (error) {
      console.error(error);
      alert('Error al crear constructora');
    }
  };

  return (
    <div>
      <h2>Crear Constructora</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div>
          <label>Dirección:</label>
          <input
            type="text"
            name="direccion"
            value={formData.direccion}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Teléfono:</label>
          <input
            type="text"
            name="telefono"
            value={formData.telefono}
            onChange={onChange}
          />
        </div>
        <div>
          <label>Representante:</label>
          <input
            type="text"
            name="representante"
            value={formData.representante}
            onChange={onChange}
          />
        </div>
        <button type="submit">Crear</button>
      </form>
    </div>
  );
}

export default CrearConstructora;
