import $ from "jquery";

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './CrearExpositor.css';  // Asegúrate de crear este archivo CSS

function CrearExpositor() {
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [fecha, setFecha] = useState("");
    const [concepto, setConcepto] = useState("");
    const navigate = useNavigate();

    const manejarEnvio = (e) => {
        e.preventDefault();
        const nuevoExpositor = { nombre, apellido, fecha, concepto };

        fetch("http://localhost:5000/api/expositores", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(nuevoExpositor),
        })
            .then((res) => res.json())
            .then(() => {
                alert("Expositor creado con éxito");
                navigate("/");
            })
            .catch((err) => console.error("Error al crear expositor:", err));
    };

    return (
        <div className="form-container">
            <h1>Crear Expositor</h1>
            <form onSubmit={manejarEnvio}>
                <div className="form-group">
                    <label htmlFor="nombre">Nombre:</label>
                    <input
                        type="text"
                        id="nombre"
                        placeholder="Nombre"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="apellido">Apellido:</label>
                    <input
                        type="text"
                        id="apellido"
                        placeholder="Apellido"
                        value={apellido}
                        onChange={(e) => setApellido(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="fecha">Fecha:</label>
                    <input
                        type="date"
                        id="fecha"
                        value={fecha}
                        onChange={(e) => setFecha(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="concepto">Concepto:</label>
                    <input
                        type="text"
                        id="concepto"
                        placeholder="Concepto"
                        value={concepto}
                        onChange={(e) => setConcepto(e.target.value)}
                        required
                    />
                </div>

                <button type="submit">Crear</button>
            </form>
        </div>
    );
}

export default CrearExpositor;
