import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function EditarProyecto() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nombre: '',
    contratista: '',
    presupuesto: '',
    carretera: '',
    fechaInicio: '',
    fechaFin: '',
  });

  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/proyectos/${id}`);
        setFormData(res.data);
      } catch (error) {
        console.error('Error al cargar el proyecto:', error);
      }
    };
    fetchProyecto();
  }, [id]);

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/proyectos/${id}`, formData);
      alert('Proyecto actualizado exitosamente');
      navigate('/proyectos');
    } catch (error) {
      console.error('Error al actualizar el proyecto:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Editar Proyecto</h2>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="form-label">Nombre:</label>
          <input
            type="text"
            className="form-control"
            name="nombre"
            value={formData.nombre}
            onChange={onChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Contratista:</label>
          <input
            type="text"
            className="form-control"
            name="contratista"
            value={formData.contratista}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Presupuesto:</label>
          <input
            type="text"
            className="form-control"
            name="presupuesto"
            value={formData.presupuesto}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Carretera:</label>
          <input
            type="text"
            className="form-control"
            name="carretera"
            value={formData.carretera}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Fecha Inicio:</label>
          <input
            type="date"
            className="form-control"
            name="fechaInicio"
            value={formData.fechaInicio}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Fecha Fin:</label>
          <input
            type="date"
            className="form-control"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Actualizar
        </button>
      </form>
    </div>
  );
}

export default EditarProyecto;
