import React, { useEffect, useState } from "react";

function ListaGaleria() {
    const [contenidos, setContenidos] = useState([]);

    // Cargar la lista de contenidos desde el backend
    useEffect(() => {
        fetch("http://localhost:5000/api/contenidos")
            .then((res) => res.json())
            .then((data) => setContenidos(data))
            .catch((err) => console.error("Error al obtener la lista de contenidos:", err));
    }, []);

    return (
        <div>
            <h2>Lista de Galerías</h2>
            {contenidos.length === 0 ? (
                <p>No hay contenidos disponibles.</p>
            ) : (
                <ul style={{ listStyleType: "none", padding: 0 }}>
                    {contenidos.map((contenido) => (
                        <li key={contenido._id} style={{ marginBottom: "20px" }}>
                            <h3>{contenido.titulo}</h3>
                            <p><strong>Expositor:</strong> {contenido.expositor?.nombre} {contenido.expositor?.apellido}</p>
                            <p><strong>Descripción:</strong> {contenido.descripcion}</p>
                            {contenido.imagen && (
                                <img
                                    src={`http://localhost:5000${contenido.imagen}`}
                                    alt={contenido.titulo}
                                    style={{ width: "300px", height: "auto", borderRadius: "8px", boxShadow: "0 2px 5px rgba(0,0,0,0.2)" }}
                                />
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default ListaGaleria;
