// frontend/src/components/EditarPresupuesto.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditarPresupuesto() {
  const { id } = useParams();
  const navigate = useNavigate();

  // Ajusta los campos según tu modelo actualizado:
  // (por ejemplo, solo "monto", "origenFondos" y "fechaFin")
  const [formData, setFormData] = useState({
    monto: 0,
    origenFondos: '',
    fechaFin: '',
  });

  // Cargar el presupuesto existente
  useEffect(() => {
    const getPresupuestoById = async () => {
      try {
        const res = await axios.get(`http://localhost:4000/api/presupuestos/${id}`);
        if (res.data) {
          setFormData({
            monto: res.data.monto || 0,
            origenFondos: res.data.origenFondos || '',
            fechaFin: res.data.fechaFin
              ? res.data.fechaFin.substring(0, 10) // Para que coincida con el <input type="date" />
              : '',
          });
        }
      } catch (error) {
        console.error(error);
        alert('Error al cargar el presupuesto');
      }
    };
    getPresupuestoById();
  }, [id]);

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/presupuestos/${id}`, formData);
      alert('Presupuesto actualizado');
      navigate('/presupuestos');
    } catch (error) {
      console.error(error);
      alert('Error al actualizar presupuesto');
    }
  };

  return (
    <div>
      <h2>Editar Presupuesto</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Monto:</label>
          <input
            type="number"
            name="monto"
            value={formData.monto}
            onChange={onChange}
            required
          />
        </div>

        <div>
          <label>Origen de Fondos:</label>
          <input
            type="text"
            name="origenFondos"
            value={formData.origenFondos}
            onChange={onChange}
          />
        </div>

        <div>
          <label>Fecha Fin (estimada):</label>
          <input
            type="date"
            name="fechaFin"
            value={formData.fechaFin}
            onChange={onChange}
          />
        </div>

        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
}

export default EditarPresupuesto;
