import React, { useState } from 'react';
import axios from 'axios';

const FormularioPremio = () => {
  const [tipo, setTipo] = useState('efectivo');
  const [descripcion, setDescripcion] = useState('');
  const [marca, setMarca] = useState('');
  const [nombreProveedor, setNombreProveedor] = useState('');
  const [apellidoProveedor, setApellidoProveedor] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const nuevoPremio = {
      tipo,
      descripcion,
      marca,
      proveedor: {
        nombre: nombreProveedor,
        apellido: apellidoProveedor,
      },
    };

    try {
      await axios.post('http://localhost:5000/premios', nuevoPremio);
      alert('Premio agregado con éxito');
      // Limpiar los campos del formulario
      setDescripcion('');
      setMarca('');
      setNombreProveedor('');
      setApellidoProveedor('');
    } catch (err) {
      alert('Error al agregar el premio');
    }
  };

  return (
    <div>
      <h3>Crear Premio</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo:</label>
          <select value={tipo} onChange={(e) => setTipo(e.target.value)}>
            <option value="efectivo">Efectivo</option>
            <option value="obsequio">Obsequio</option>
          </select>
        </div>
        <div>
          <label>Descripción:</label>
          <input
            type="text"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Marca:</label>
          <input
            type="text"
            value={marca}
            onChange={(e) => setMarca(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Nombre del Proveedor:</label>
          <input
            type="text"
            value={nombreProveedor}
            onChange={(e) => setNombreProveedor(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Apellido del Proveedor:</label>
          <input
            type="text"
            value={apellidoProveedor}
            onChange={(e) => setApellidoProveedor(e.target.value)}
            required
          />
        </div>
        <button type="submit">Agregar Premio</button>
      </form>
    </div>
  );
};

export default FormularioPremio;
